package com.syntax.class01;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//whole numbers
		
		byte box1= 21;
		short box2= 2022;
		int box3= 76666;  // mostly we will use these
		long box4= 2749827348273894L;
		
		//decimal numbers
		
		float container1=1.99f;
		double container2=89.99;
		
		//to represent 1 character 
		
		char gender= 'm';
		char dollar= '$';
		
		// to represent true or false
		
		boolean hungry= true;
		boolean sleepy= false;
		
		System.out.println(box1); // don't need double quotes ""
		System.out.println(box2);
		System.out.println(box3);
		System.out.println(box4);
		System.out.println(container1);
		System.out.println(container2);
		System.out.println(gender);
		System.out.println(dollar);
		System.out.println(hungry);
		System.out.println(sleepy);
		
	}

}
