package com.syntax.class08;

public class ForLoopExamples {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 
		
		 // I need to print numbers from 1 to 60, example 1
		
		
		for(int i=1; i<=60; i++) {
			
			System.out.print(i+ " ");
		}
		
		System.out.println();
		// I need numbers from 60 to 10, example 2
		
		for (int j=60; j>=10; j--) {
			
			System.out.print(j+ " ");
		}
		
		System.out.println(); // example 3
		
		for(int k=5; k<=40; k+=5) {
			System.out.print(k+ " ");
		}
			
			
	}

}
